//------------------------------------------------------------------------------
// Private
//------------------------------------------------------------------------------

var parentClass = require('toolButtons').theclass; 

//==============================================================================
// The Class
//==============================================================================

/**
 * Constructor
 * 
 * @param   {object} options    The JS object with initial class parameters
 * @returns {object}            A new instance of this class
 */
var __theClass = function (options) {

    if (options) {
        options.frameControlName = options.frameControlName || 'Star';
    }
    
    parentClass.apply(this, arguments);
    
    return this;

};

__theClass.prototype             = Object.create(parentClass.prototype);
__theClass.prototype.constructor = __theClass;

//__theClass.prototype = {

//------------------------------------------------------------------------------
// Config
//------------------------------------------------------------------------------


//------------------------------------------------------------------------------
// Protected
//------------------------------------------------------------------------------



//------------------------------------------------------------------------------
// Public
//------------------------------------------------------------------------------

//};

//==============================================================================
// Exports module values:
//==============================================================================

module.exports = {
    
    /**
     * The class to inherit
     * 
     * example:
     * 
     * function childClass() {
     *      theclass.apply(this, arguments);
     * }
     * 
     * childClass.prototype = Object.create(theclass.prototype);
     * childClass.prototype.constructor = childClass;
     * 
     */
    theclass: __theClass,
    
    create: function (options) { 
        return new __theClass(options); 
    },
    
    preload: function (game) {
        game.load.atlas('toolButtons', 'assets/sprites/controls/tools1.png', 'assets/sprites/controls/tools.json');
    }
    
};