//==============================================================================
// Exports module values:
//==============================================================================

module.exports = {

    setBlockProperties: function(sprite, tile) {
        var block_type;
        if (tile && tile.properties && tile.properties.block_type) block_type = tile.properties.block_type;
        switch (block_type) {
            case 'test':
                sprite.body.mass      = 50;
                sprite.body.gravity.y = 0;
                sprite.body.bounce.y  = 0.5;
                sprite.body.collideWorldBounds = true;                
                    break;
            default:
                sprite.body.mass      = 100;
                sprite.body.gravity.y = 300;
                sprite.body.bounce.y  = 0.5;
                sprite.body.collideWorldBounds = true;                
        }
    }

};