var localStorage = require('localStorage');

var data = {
    
    //--- Game controls:
    soundEnable : true,
    musicEnable : true,
    gameControl : 0,    //0=keyborad, 1-gamepad, 2-gamepad2
    lang        : 'en', //en|ru
    gameName    : 'game_default'
    
    //--- Player data:
    //scene       : '',
    //map         : null,
    //positionX   : null,
    //positionY   : null,
    //collected   : [],
    //firestate   : 0,
    //gamestate   : {}
    
};

var storageName;

//==============================================================================
// Exports module values:
//==============================================================================

module.exports = {
    
    set: function (name, value) {
        data[name] = value;
        localStorage.save(storageName, data);
    },
    
    get: function (name) {
        return data[name];
    },
    
    load: function (sname) {
        storageName = sname || "globals"; 
        Phaser.Utils.extend(true, data, localStorage.load(storageName)); 
    }

};