var toolButtons = require('toolButtons');

//==============================================================================
// Exports module values:
//==============================================================================

module.exports = {
    
    preload: function(game) {
        
        toolButtons.preload(game);
        
    },
    
    create: function(game) {

        //--- Tools buttons: 
        var tb = toolButtons.create({game:game, x:10, y:10, 
            frameControlName : null,
            buttons:{
                Fullscreen:{ 
                    state:  game.scale.isFullScreen, 
                    onDown: function(o) { 
                                game.extentions.sceneManager.gotoFullScreen();
                                o.setState(game.scale.isFullScreen);
                                //--- Trigger an event handler:
                                if (typeof(this.onResize) === 'function') this.onResize();
                             }.bind(this) 
                }
            }
        });
        //-------------------------------- 
        
        return tb;
    },
    
    //--- An event handler:
    onResize: null

};